import Link from "next/link"
import logo from "../assets/exam.png"
export default ({loggedIn, accountName, userID}) => (
    <div className="main">
            <Link href="/">
                <a>
                    <img className="logo" src={logo} alt="logo" />
                </a>
            </Link>

            {
                loggedIn ? <Link href={`/user/${userID}`}><a className="account">{accountName}</a></Link> : 
                <div className="account">
                    <Link href="/login"><a>Login</a></Link> <Link href="/signup"><a>Sign up</a></Link>
                </div>
            }

    <style jsx>{`
        .main {
            position: relative;
            display: flex;
            flex-direction: column;
            justify-content: center;
            height: 3rem;
            background: #2C404D;
        }

        .account {
            align-self: flex-end;
            display: flex;
            width: 20%;
            justify-content: space-around;
        }

        .logo {
            position: absolute;
            top: .5rem;
            left: .5rem;
            width: 32px;
            height: 32px
        }

        a {
            text-decoration: none;
            color: #232b2b;
        }

        a:hover {

        }

    `}</style>
    </div>
)
