const InputWithSidebar = (props) => (
    <div style={{position: "relative", display: "block"}}>
        <div style={{width: "100%"}}>
        {props.children}
        </div>
        <div style={{position: "absolute", top: "0%", left: "100%", background: "ye"}}>
            <ul style={{padding: "0"}}>
            {props.errors.map(err => (
                <li>{err}</li>
            ))}
            </ul>
        </div>
    </div>
)
class signup extends React.Component {

    onClick = () => {
        console.log("clicked!")
    }

    render() {
        return (
            <div className= "mainBody">
                <div className="createAccountMain">
                    <div className="container">
                        <h1 style={{color: "#E8F4F8", margin: "1rem", marginTop: "2rem", fontSize: "2.3rem"}}>Create an Account</h1>
                        <InputWithSidebar errors={["Bad name", "Bad"]}>
                        <input type="text" placeholder="Username"/>
                        </InputWithSidebar>
                        <input type="text" placeholder="Email"/>
                        <input type="password" placeholder="Password"/>
                        <input type="password" placeholder="Re-type Password"/>
                        <button type="button" onClick={this.onClick}>Signup</button>
                    </div>

                </div>
            <style jsx>{`
                .container {
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;
                    align-items: center;
                    height: 100%;
                    width: 70%
                }
                .createAccountMain {
                    width: 60%;
                    height: 85%;
                    background: blue;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    border-radius: 10px;
                    background: rgb(18, 65, 196);
                    -webkit-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    -moz-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                }

                .mainBody {
                    height: 40rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }

                input {
                    height: 2.5rem;
                    width: 80%;
                    border-radius: 25px;
                    border: 3px solid rgba(0,0,0,0);
                    font-size: 1.2rem;
                    background: rgba(201, 222, 255, .6);
                    padding-left: 1rem;
                    transition: border-radius 1s, border 1s;
                }

                input::placeholder {
                    text-align: center;
                    margin-left: -1rem;
                    font-size: 1.2rem;
                    color: #E8F4F8

                }
                input:focus {
                    outline: none;
                    border-radius: 10px;
                    border: 1px solid white
                }

                button {
                    border-radius: 10px;
                    height: 3rem;
                    width: 16rem;
                    background: rgb(255,255,255);
                    highlight: none;
                    border: 3px solid rgba(0,0,0,0);
                    margin: 1.5rem;
                    margin-top: 0;
                    font-size: 1.3rem;
                    -webkit-box-shadow: 0px 0px 8px 3px rgba(50,50,50,0.5);
                    -moz-box-shadow: 0px 0px 8px 3px rgba(50,50,50,0.5);
                    box-shadow: 0px 0px 8px 3px rgba(50,50,50,0.5);
                    transition: -webkit-box-shadow .2s, -moz-box-shadow .2s, box-shadow .2s
                }

                button:active {
                    background: #E8F4F8;
                    -webkit-box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.75);
                    -moz-box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.75);
                    box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.75);
                }

                button:focus {
                    outline: none;
                }

                h1 {
                    margin-bottom: 3rem;
                    margin-top: 2rem
                }
            `}</style>
            </div>
        )
    }
}

export default signup
