import {Formik, Field, Form} from 'formik'

class signup extends React.Component {

    render() {
        return (
            <div className= "mainBody">
                    <div className="createAccountMain">
                        <h1>Create an Account</h1>
                        <Formik 
                        initialValues = {{email: "", password: "", username: "", confirmPassword: ""}} 
                        validate = {values => {
                            return {}
                        }}
                        >
                            {({values, handleChange, handleBlur}) => (
                                <Form >
                                    <div style={{display: "flex", flexDirection: "column", alignItems: "center"}}>
                                        <label>Username</label>
                                        <Field name="username" type="input" />
                                    </div>
                                    <div>
                                    <Field name="email" type="input" />
                                    </div>
                                    <div>
                                    <Field name="password" type="input" />
                                    </div>
                                    <div>
                                    <Field name="confirmPassword" type="input" />
                                    </div>
                                </Form>
                            )}
                        </Formik>
                    </div>
            <style jsx>{`
                .createAccountMain {
                    width: 60%;
                    height: 85%;
                    background: blue;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    border-radius: 10px;
                    background: rgba(201, 222, 255, 1);
                    -webkit-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    -moz-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                }

                .mainBody {
                    height: 40rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }
                label {
                    align-self: flex-start
                }
            `}</style>
            </div>
        )
    }
}

export default signup
