export default ({loggedIn, accountName, userID}) => (
    <div className="main">
        <div className="account">
            {
                loggedIn ? <a href={`localhost:3000/user/${userID}`}>{accountName}</a> : <div className="login">
                    <div><a href="localhost:3000/login">Login</a> </div><div><a href="localhost:3000/signup">Sign up</a></div>
                </div>
            }
        </div>

    <style jsx>{`
        .main {
            display: flex;
            flex-direction: column;
            background: #2C404D;
            height: 3rem;
            justify-content: center;
        }
        .account {
            display: flex;
            flex-direction: column;
            align-items: flex-end;
            align-self: flex-end;
            width: 20%;
            margin-right: ${loggedIn ? 0 : '1rem'};
        }
        .login {
            display: flex;
            justify-content: space-around;
        }

    `}</style>
    </div>
)
