import {Formik,Form} from 'formik'
import axios from "axios"
import * as yup from "yup"


//Do validation
class signup extends React.Component {

    onSubmit = (data, {setSubmitting}) => {
        console.log("submit!")
        setSubmitting(true);
        axios.post("/createUser", {
            username: data.username,
            email: data.email,
            password: data.password,
            schoolID: ""
        })
        .then(res => {
            console.log(res)
            setSubmitting(false)
        })
        .catch(err => {
            console.log(err)
            setSubmitting(false)
        })
    }

    render() {
        return (
            <div className= "mainBody">
                    <div className="createAccountMain">
                        <h1 style={{position: "absolute",top: "1rem", fontSize: "35px" }}>Create an Account</h1>
                        <div className="container">
                        <Formik 
                        initialValues = {{email: "", password: "", username: "", confirmPassword: ""}} 
                        validate = {values => {
                            const errors = {}
                            errors.password = []
                            if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(values.email) == false) errors.email = "Please enter a valid email address"
                            const lw = new RegExp("/[a-z]/") //Checks for lowercase chars
                            const up = new RegExp("/[A-Z]/") //Checks for uppercase chars
                            const dig = new RegExp("/[0-9]/") //Checks for Digits
                            if (values.password.length < 6) errors.password.concat("Must be longer than 6 characters")
                            if (!lw.test(values.password)) errors.password.concat("Must contain a lowercase character")
                            if (!up.test(values.password)) errors.password.concat("Must contain an uppercase character")
                            if (!dig.test(values.password)) errors.password.concat("Must contain a number")
                            return errors
                        }}
                        onSubmit={this.onSubmit}
                        >
                            {({values, handleChange, handleBlur, isSubmitting, onSubmit}) => (
                                <Form >
                                    <div className="fields">
                                        <label>Username</label>
                                        <input name="username" type="text" value={values.username} onChange={handleChange} onBlur={handleBlur} />
                                    </div>
                                    <div className="fields">
                                        <label>Email</label>
                                        <input name="email" type="email" value={values.email} onChange={handleChange} onBlur={handleBlur} />
                                    </div>
                                    <div className="fields">
                                        <label>Password</label>
                                        <input name="password" type="password" value={values.password} onChange={handleChange} onBlur={handleBlur} />
                                    </div>
                                    <div className="fields">
                                        <label>Confirm Password</label>
                                        <input name="confirmPassword" type="password" value={values.confirmPassword} onChange={handleChange} onBlur={handleBlur} />
                                    </div>
                                    <div className="buttonContainer">
                                        <button disabled={isSubmitting} type="submit">Sign Up</button>
                                    </div>
                                </Form>
                            )}
                        </Formik>
                        </div>
                    </div>
            <style jsx>{`
                .createAccountMain {
                    width: 60%;
                    height: 100%;
                    margin: auto 0 auto 0;
                    background: blue;
                    display: flex;
                    flex-direction: column;
                    position: relative;
                    align-items: center;
                    border-radius: 10px;
                    background: rgb(201, 222, 255);
                    -webkit-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    -moz-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                }
                .mainBody {
                    height: 40rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }
                .container {
                    margin: auto 0 auto 0;
                    padding-top: 25px
                }
                input {
                    align-self: flex-start;
                    border-color: rgba(256,256,256, 0);
                    background-color: rgba(256,256,256, 0);
                    border-bottom: 1px solid black;
                    transition: border-bottom .3s;
                    width: 20rem;
                    font-size: 18px;
                }
                input:focus {
                    outline: none;
                    border-bottom: 1px solid white;
                }
                label {
                    align-self: flex-start;
                    font-size: 25px;
                    display: inline-block;
                    margin-bottom: 16px
                }
                .fields {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    margin: 0 0 1rem 0;
                }
                .buttonContainer {
                    display: flex;
                    align-items: center;
                    justify-content: center
                }
                button {
                    border: 1px solid rgba(0,0,0, 1);
                    border-radius: 10px;
                    background-color: rgba(0,0,0,0);
                    width: 40%;
                    height: 2rem;
                    -webkit-box-shadow: 0px 0px 1px 1px rgba(0,0,0,0.75);
-moz-box-shadow: 0px 0px 1px 1px rgba(0,0,0,0.75);
box-shadow: 0px 0px 1px 1px rgba(0,0,0,0.75);
                }
                button:active {
                    outline: none;
                    background-color: rgb(182, 205, 242);
                    -webkit-box-shadow: inset 0px 0px 1px 1px rgba(0,0,0,0.75);
                    -moz-box-shadow: inset 0px 0px 1px 1px rgba(0,0,0,0.75);
                    box-shadow: inset 0px 0px 1px 1px rgba(0,0,0,0.75);
                }
                button:hover {
                    cursor: pointer
                }
                button:focus {
                    outline:none
                }

            `}</style>
            </div>
        )
    }
}

export default signup
