import {Formik, Field, Form} from 'formik'

class signup extends React.Component {

    render() {
        return (
            <div className= "mainBody">
                    <div className="createAccountMain">
                        <h1>Create an Account</h1>
                        <Formik 
                        initialValues = {{email: "", password: "", username: "", confirmPassword: ""}} 
                        validate = {values => {
                            return {}
                        }}
                        >
                            {({values, handleChange, handleBlur}) => (
                                <Form >
                                    <div style={{display: "flex", flexDirection: "column", alignItems: "center"}}>
                                        <label>Username</label>
                                        <Field name="username" type="input" />
                                    </div>
                                    <div>
                                    <Field name="email" type="input" />
                                    </div>
                                    <div>
                                    <Field name="password" type="input" />
                                    </div>
                                    <div>
                                    <Field name="confirmPassword" type="input" />
                                    </div>
                                </Form>
                            )}
                        </Formik>
                    </div>
            <style jsx>{`
                .createAccountMain {
                    width: 60%;
                    height: 85%;
                    background: blue;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    border-radius: 10px;
                    background: rgba(201, 222, 255, 1);
                    -webkit-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    -moz-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                }

                .mainBody {
                    height: 40rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }

                input {
                    height: 2.5rem;
                    width: 100%;
                    border-radius: 25px;
                    border: 3px solid rgba(0,0,0,0);
                    font-size: 1.2rem;
                    background: rgba(201, 222, 255, .6);
                    padding: 0 1rem 0 1rem;
                    transition: border-radius 1s, border 1s;
                }

                input::placeholder {
                    text-align: center;
                    margin-left: -1rem;
                    font-size: 1.2rem;
                    color: #E8F4F8

                }
                input:focus {
                    outline: none;
                    border-bottom: 1px solid white
                }

                button {
                    border-radius: 10px;
                    height: 3rem;
                    width: 16rem;
                    background: rgb(255,255,255);
                    highlight: none;
                    border: 3px solid rgba(0,0,0,0);
                    margin: 1.5rem;
                    margin-top: 0;
                    font-size: 1.3rem;
                    -webkit-box-shadow: 0px 0px 8px 3px rgba(50,50,50,0.5);
                    -moz-box-shadow: 0px 0px 8px 3px rgba(50,50,50,0.5);
                    box-shadow: 0px 0px 8px 3px rgba(50,50,50,0.5);
                    transition: -webkit-box-shadow .2s, -moz-box-shadow .2s, box-shadow .2s
                }

                button:active {
                    background: #E8F4F8;
                    -webkit-box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.75);
                    -moz-box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.75);
                    box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.75);
                }

                button:focus {
                    outline: none;
                }

                h1 {
                    margin-bottom: 3rem;
                    margin-top: 2rem
                }
            `}</style>
            </div>
        )
    }
}

export default signup
