import {Formik, Field, Form} from 'formik'

class signup extends React.Component {

    render() {
        return (
            <div className= "mainBody">
                    <div className="createAccountMain">
                        <h1 style={{position: "absolute",top: "2.5rem" }}>Create an Account</h1>
                        <div className="container">
                        <Formik 
                        initialValues = {{email: "", password: "", username: "", confirmPassword: ""}} 
                        validate = {values => {
                            return {}
                        }}
                        >
                            {({values, handleChange, handleBlur}) => (
                                <Form >
                                    <div className="fields">
                                        <label>Username</label>
                                        <Field name="username" type="input" as="input" />
                                    </div>
                                    <div className="fields">
                                    <label>Email</label>
                                    <Field name="email" type="input" />
                                    </div>
                                    <div className="fields">
                                    <label>Password</label>

                                    <Field name="password" type="input" />
                                    </div>
                                    <div className="fields">
                                    <label>Confirm Password</label>

                                    <Field name="confirmPassword" type="input" />
                                    </div>
                                </Form>
                            )}
                        </Formik>
                        </div>
                    </div>
            <style jsx>{`
                .createAccountMain {
                    width: 60%;
                    height: 85%;
                    background: blue;
                    display: flex;
                    flex-direction: column;
                    position: relative;
                    align-items: center;
                    border-radius: 10px;
                    background: rgba(201, 222, 255, 1);
                    -webkit-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    -moz-box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                    box-shadow: 1px 0px 14px 3px rgba(0,0,0,0.75);
                }

                .mainBody {
                    height: 40rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }

                .container {
                    margin: auto 0 auto 0;
                }
                input {
                    align-self: flex-start;
                    font-size: 50rem;
                    border-radius: 25px;
                    color: blue 
                }

                label {
                    align-self: flex-start;
                    font-size:18px
                }

                .fields {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    margin: 0 0 1rem 0
                }
            `}</style>
            </div>
        )
    }
}

export default signup
