import Link from "next/link"
import Head from "next/head"
import axios from "axios"

/*
TODOS:
- Resize search bar depending on how big the width of the screen is and if the user is logged in or not
- Move the search bar farther away from the account Info section
- Style Search bar
- Add Test Logo in
*/
class Navbar extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            search: "",
            results: []
        }
    }

    onType = (e) => {
        this.setState({search: e.target.value})
        if (e.target.value.length <= 1) return;
        axios.get(`http://localhost:8080/searchProf/${e.target.value}`)
        .then(data => {
            this.setState({results: data.data.professors})
            console.log(this.state.results)
        })
        .catch(err => {
            console.error(err)
        })
    }

    render() {
        const {loggedIn, accountName, userID} = this.props
        return (
            <div className="main">
        <Head>
            <link href="https://fonts.googleapis.com/icon?family=Material+Icons" rel="stylesheet" />
        </Head>
            <div className="searchMain">
                <div className="icon">
                    <i className="material-icons">search</i>
                </div>
                <input type="text" value={this.state.search} onChange={this.onType} placeholder="Search University, Professor, or Course..."></input>
                {this.state.results.map((prof) => {
                        <Link href={`/professor/${prof._id}`}>
                            <a>
                                <div style={{width: "100%"}}>
                                    <h1 style={{color: "white"}}>{prof.name}</h1>
                                </div>
                            </a>
                        </Link>
                })}
            </div>
            <Link href={loggedIn ? '/liked' : 'login'}>
                <a className="heart">
                    <i className="material-icons">{loggedIn ? "favorite" : "favorite_border"}</i>
                </a>
            </Link>
            <Link href={loggedIn ? '/upload' : 'login'}>
                <a className='flex'>
                    <i className="material-icons">file_upload</i>
                </a>
            </Link>
            {loggedIn ? <Link href={`/user/${userID}`}><a>{accountName}</a></Link> : null}
            {!loggedIn ? <Link href="/login"><a>Login</a></Link> : null}
            {!loggedIn ? <Link href="/signup"><a>Sign up</a></Link> : null}
    <style jsx>{`
        .main {
            position: relative;
            display: flex;
            flex-direction: row;
            justify-content: flex-end;
            align-items: center;
            height: 6rem;
            background: rgb(0,0,0);
            padding: 0 2rem 0 2rem;
            border-bottom-left-radius: 25px;
            border-bottom-right-radius: 25px
        }

        .searchMain {
            position: absolute;
            width: 18.5rem;
            height: 1.7rem;
            left: 50%;
            top: 50%;
            margin-left: -8rem;
            margin-top: -.85rem;
        }

        .logo {
            position: absolute;
            top: .65rem;
            left: .5rem;
            width: 32px;
            height: 32px
        }

        a {
            text-decoration: none;
            color: rgb(255,255,255);
            font-size: 1.4rem;
            margin: 0 .4rem 0 .4rem;
            letter-spacing: .09rem
        }

        a:hover {
            color: rgb(222, 218, 217);
        }

        .flex {
            display: flex;
            width: 24px;
        }

        .heart {
            display: flex;
            width: 24px;
            color: rgb(240, 79, 79);
        }

        .heart:hover {
            color: rgb(207, 58, 58)
        }

        input {
            font-family: 'Raleway', sans-serif;
            height: 100%;
            width: 100%;
            font-size: 1rem;
            border-radius: 25px;
            padding-left: 1.7rem;
            padding-right: 1rem
        }

        input:focus {
            outline: none
        }

        .icon {
            display: inline-block;
            position: absolute;
            top: 50%;
            margin-top: -.6rem;
            left: .2rem
        }
    `}</style>
    </div>
        )
    }
}

export default Navbar
